// ==UserScript==
// @name         Genshin Resin
// @version      1.0
// @description  Adds a button to view info about original resin on hoyolab
// @author       Super Zombi
// @match        https://www.hoyolab.com/*
// @icon         https://www.google.com/s2/favicons?sz=64&domain=hoyolab.com
// @homepageURL  https://github.com/SuperZombi/genshin-resin-api
// @supportURL   https://github.com/SuperZombi/genshin-resin-api/issues
// @updateURL    https://raw.githubusercontent.com/SuperZombi/genshin-resin-api/main/genshin-resin.user.js
// @downloadURL  https://raw.githubusercontent.com/SuperZombi/genshin-resin-api/main/genshin-resin.user.js
// @grant        GM_addStyle
// ==/UserScript==

(function() {
    'use strict';

    function getCookie(name) {
        const value = `; ${document.cookie}`;
        const parts = value.split(`; ${name}=`);
        if (parts.length === 2) return parts.pop().split(';').shift();
    }

    const images = {
        "original_resin.png": "data:image/png;base64,"
    }

    const locale = {
        'en': {
            "full_recover": "Fully replenished",
            "next_recover": "Replenished in"
        },
        'ru': {
            "full_recover": " ",
            "next_recover": ". "
        }
    }

    function get_message(name, default_="en"){
        var userLang = (getCookie("mi18nLang") || navigator.language || navigator.userLanguage).slice(0,2).toLowerCase();
        if (Object.keys(locale).includes(userLang) && Object.keys(locale[userLang]).includes(name)){
            return locale[userLang][name]
        }
        return locale[default_][name]
    }

    GM_addStyle ( `
        #resin_plugin {
            position: fixed;
            bottom: 0px;
            right: 0px;
            z-index: 100;
            height: 60px;
            width: 60px;
            transform: translateY(25%);
            background: rgba(0, 192, 255, 0.5);
            opacity: 0.75;
            border-radius: 18px 18px 0px 0px;
            transition: 0.3s;
            cursor: pointer;
            display: flex;
            font-family: sans-serif;
        }
        #resin_plugin:hover{
            transform: translateY(0);
            opacity: 1;
        }
        #resin_plugin.active{
            transform: translateY(0);
            opacity: 1;
            cursor: wait;
        }
        #resin_plugin.error{
            background: rgba(255, 25, 25, 0.75);
        }
        #resin_plugin.transparent{
            background: unset;
            border-radius: 0;
            cursor: auto;
            height: unset;
            width: unset;
        }

        #resin{
            display: flex;
            flex-direction: column;
            align-items: center;
            position: relative;
            user-select: none;
            margin: 5px;
        }
        #resin-area{
            background: rgb(100, 100, 100, 0.6);
            display: inline-flex;
            align-items: center;
            justify-content: center;
            gap: 4px;
            border-radius: 50px;
            color: white;
        }
        #resin-area > img{
            height: 40px;
        }
        #resin-area > .text{
            padding-right: 10px;
        }
        #resin-description{
            display: inline-flex;
            flex-direction: column;
            gap: 4px;
            background: rgb(0, 0, 0, 0.75);
            color: white;
            padding: 10px;
            border-radius: 8px;
            visibility: hidden;
            opacity: 0;
            transition: 0.5s;
            position: absolute;
            bottom: calc(100% + 6px);
            right: 0;
        }
        #resin-description > .text{
            color: #d0be8e;
            white-space: nowrap;
        }
        #resin:hover #resin-description{
            visibility: visible;
            opacity: 1;
        }
    ` );

    function loader(size="100%"){
        return `
        <svg xmlns="http://www.w3.org/2000/svg" style="display:block;margin:auto;" width="${size}" height="${size}" viewBox="0 0 100 100">
            <circle cx="50" cy="50" fill="none" stroke="#007EFF" stroke-width="10" r="35" stroke-dasharray="165 57">
                <animateTransform attributeName="transform" type="rotate" repeatCount="indefinite" dur="1s" values="0 50 50;360 50 50" keyTimes="0;1"></animateTransform>
            </circle>
        </svg>`
    }

    window.addEventListener("load", _=>{
        if (document.getElementById("resin_plugin")){return}
        let div = document.createElement("div")
        div.id = "resin_plugin"
        div.innerHTML = `
            <img src="${images["original_resin.png"]}" height="100%" width="100%">
        `
        div.onclick = _=>{
            div.onclick = null;
            makeRequest(div)
        }
        document.body.appendChild(div)
    })

    function makeRequest(div){
        function intToTime(totalSeconds){
            let hours = String(Math.floor(totalSeconds / 3600)).padStart(2, '0');
            totalSeconds %= 3600;
            let minutes = String(Math.floor(totalSeconds / 60)).padStart(2, '0');
            let seconds = String(totalSeconds % 60).padStart(2, '0');
            return `${hours}:${minutes}:${seconds}`
        }
        function updateTime(newTime){
            if (newTime % 480 == 0){
                let resign = div.querySelector("#resin #resin-area > .text")
                let currentResin = parseInt(resign.getAttribute("current")) + 1
                resign.setAttribute("current", currentResin)
                resign.innerHTML = `${currentResin}/160`
            }
            let description = div.querySelector("#resin #resin-description")
            description.querySelector("#full-recover").innerHTML = intToTime(newTime)
            description.querySelector("#next-recover").innerHTML = intToTime(newTime % 480)

            if (newTime <= 0){
                return
            }
            setTimeout(_=>{
                updateTime(newTime - 1)
            }, 1000)
        }
        function initResinTimer(currentResin, fullRecover){
            let resign = div.querySelector("#resin #resin-area > .text")
            resign.setAttribute("current", currentResin)
            resign.innerHTML = `${currentResin}/160`
            updateTime(fullRecover)
        }

        div.classList.remove("error")
        div.classList.add("active")
        div.innerHTML = loader("80%")

        let xhr = new XMLHttpRequest();
        const target = new URL('https://genshin-api.superzombi.repl.co/getOriginalResin');
        const params = new URLSearchParams();
        params.set('ltuid', getCookie('ltuid'));
        params.set('ltoken', getCookie('ltoken'));
        target.search = params.toString();
        xhr.open("GET", target.href, true)
        xhr.onload = _=>{
            if (xhr.status == 200){
                let answer = JSON.parse(xhr.response);
                div.classList.add("transparent")
                div.innerHTML = `
                    <div id="resin">
                        <div id="resin-description">
                            <span class="text">${get_message("full_recover")}</span>
                            <span class="time" id="full-recover">00:00:00</span>
                            <div style="height: 5px"></div>
                            <span class="text">${get_message("next_recover")}</span>
                            <span class="time" id="next-recover">00:00:00</span>
                        </div>
                        <div id="resin-area">
                            <img src="${images["original_resin.png"]}">
                            <span class="text" current="0">0/160</span>
                        </div>
                    </div>
                `
                initResinTimer(answer.current_resin, answer.full_recover)
            } else{
                console.error("Error fetching /getOriginalResin")
                div.classList.remove("active")
                div.classList.add("error")
                setTimeout(_=>{div.classList.remove("error")}, 2000)
                div.innerHTML = `
                    <img src="${images["original_resin.png"]}" height="100%" width="100%">
                `
                div.onclick = _=>{
                    div.onclick = null;
                    makeRequest(div)
                }
            }
        }
        xhr.send()
    }

})();
